import { Query } from '@apollo/react-components';
import { gql } from 'apollo-boost';
import React from 'react';


class Auth {

	constructor(){
		this.authenticated = false;
	}

	userInfo(cb) {
		this.authenticated = true;
		cb()
	}

	logout() {
		this.authenticated = false;
	}

	isAuthenticated() {
		return this.authenticated;
	}


}


const USERINFO = gql`
{
  userInfo(username:"amalsan"){
    username
    wpm
  }
}`


