import { Query } from '@apollo/react-components';
import { gql } from 'apollo-boost';


class Auth {

	constructor(){
		this.authenticated = false;
	}

	userInfo() {
		<Query query={USERINFO}>
		{({ loading, error, data }) => {
		      if (loading) return <p>Loading...</p>;
		      if (error) return (this.authenticated = false)
		      if (data) return (this.authenticated = true)
		    }}
		  </Query>

	}

	logout() {
		this.authenticated = false;
	}

	isAuthenticated() {
		return this.authenticated;
	}


}


const USERINFO = gql`
{
  userInfo(username:"amalsan"){
    username
    wpm
  }
}`


// const USERUPDATE;


// const USERDELETE; 


// const USERADD;


// const USERLIST;


export default new Auth();
